      fragmentShader: `
      uniform float u_intensity;
      uniform float u_time;

      varying vec2 vUv;
      varying float vDisplacement;

      void main() {
          float distort = 2.0 * vDisplacement * u_intensity * sin(vUv.y * 10.0 + u_time);
          vec3 color = vec3(abs(vUv - 0.5) * 2.0  * (1.0 - distort), 1.0);
          gl_FragColor = vec4(color, 1.0);
      }

      `,
    });
  }

  setMesh() {
    if (!this.app) {
      this.app = {};
    }

    this.app.clock = new THREE.Clock();
    this.mesh = new THREE.Mesh(this.geometry, this.material);
    this.scene.add(this.mesh);
  }

  setPosition() {
    this.mesh.position.x = this.position.x;
    this.mesh.position.y = this.position.y;
    this.mesh.position.z = this.position.z;
  }
  averageLast10(array) {
    let sum = 0;
    let count = 0;
    for (let i = array.length - 1; i >= 0 && count < 10; i--) {
      sum += array[i];
      count++;
    }
    return sum / count;
  }

  update() {
    let currentKick = this.app.LoadSound.kick;
    console.log("sadadsa");
    if (
      this.kickValues.length === 0 ||
      this.kickValues[this.kickValues.length - 1] !== currentKick
    ) {
      this.kickValues.push(currentKick);
    }
    if (this.kickValues.length > 30) {
      this.kickValues.shift();
    }

    function averageLast10(array) {
      let sum = 0;
      let count = 0;
      for (let i = array.length - 1; i >= 0 && count < 30; i--) {
        sum += array[i];
        count++;
      }
      return sum / count;
    }

    let average = averageLast10(this.kickValues);

    if (this.mesh || currentKick < 1) {
      this.mesh.material.uniforms.u_time.value =
        0.4 * this.app.clock.getElapsedTime();
    }
    if (
      this.app.LoadSound.kick > average + 5 ||
      this.app.LoadSound.kick > 244
    ) {
      this.mesh.material.uniforms.u_intensity.value = MathUtils.lerp(
        this.mesh.material.uniforms.u_intensity.value,
        this.mesh ? 1 : 0.15,
        this.app.LoadSound.kick / 600
      );
    } else {
      this.mesh.material.uniforms.u_intensity.value = MathUtils.lerp(
        this.mesh.material.uniforms.u_intensity.value,
        0.15,
        0.1
      );
    }
  }
}

